insert into vo_virhetype (id, ordinal, label_fi, label_sv, description_fi, description_sv)
values
(0, 1, 'Ei laskennalliset / Todistus tehty laajennuksesta', 'Ei laskennalliset / Todistus tehty laajennuksesta SV', 'Energiatodistus on laadittu virheellisesti rakennuksen laajennuksesta, eikä koko rakennuksesta. Todistus tehdään rakennuksen osasta vain silloin, kuin rakennuksen eri osien käyttötarkoitukset poikkeavat olennaisesti toisistaan. ARA suosittelee, että rakennuksesta laaditaan uusi koko rakennusta koskeva todistus käyttöönoton jälkeen. Tällöin todistus laaditaan olemassa olevalle rakennukselle sisältäen havainnointikäynnin paikan päällä. (Laki rakennuksen energiatodistuksesta 50/2013, 4 §)', 'TODO SV'),
(1, 2, 'Ei laskennalliset / Kerrosala alle 50 m²', 'Ei laskennalliset / Kerrosala alle 50 m² SV', 'Energiatodistus on laadittu nettoalaltaan  alle 50 m²:n suuruiselle rakennukselle. Onko rakennuksen kerrosala alle 50 m²? Kerrosalaltaan alle 50 m²:n suuruisille rakennuksille ei tehdä energiatodistusta. (Laki rakennuksen energiatodistuksesta 50/2013, 3 §)', 'TODO SV'),
(2, 3, 'Ei laskennalliset / Paritalon puolikkaasta tehty todistus', 'Ei laskennalliset / Paritalon puolikkaasta tehty todistus SV', 'Energiatodistus on laadittu ainoastaan paritalon toisesta asunnosta. ARAn linjauksen mukaan todistus tulee kuitenkin laatia koko rakennukselle silloin kun rakennuksella on yksi pysyvä rakennustunnus.', 'TODO SV'),
(3, 4, 'Ei laskennalliset / Autotallin pinta-ala laskettu mukaan', 'Ei laskennalliset / Autotallin pinta-ala laskettu mukaan SV', 'Netto-alaan on virheellisesti huomioitu mukaan autotallin pinta-ala.  Energiatodistukseen ei huomioida rakennuksen sisällä sijaitsevia tai rakennukseen rakenteellisesti liittyviä moottoriajoneuvosuojia. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(4, 5, 'Ei laskennalliset / Rakennustunnus puuttuu/on virheellinen', 'Ei laskennalliset / Rakennustunnus puuttuu/on virheellinen SV', 'Pysyvä rakennustunnus [puuttuu/on virheellinen]. Rakennustunnuksena esitetään väestötietojärjestelmän mukainen pysyvä rakennustunnus. Pysyvät rakennustunnukset löytyvät esimerkiksi SYKEen ylläpitämästä Liiteri-karttapalvelusta. (Ympäristöministeriön asetuksen rakennuksen energiatodistuksesta 1048/2017, Liite 4)', 'TODO SV'),
(5, 6, 'Ei laskennalliset / Todistus tehty väärän lain mukaan I', 'Ei laskennalliset / Todistus tehty väärän lain mukaan I SV', 'Käyttöönottovaiheen energiatodistus tulee päivittää saman lain mukaiseksi kuin lupavaiheen energiatodistus on ollut.  (Energiatodistuslaki 50/2013 30 §)', 'TODO SV'),
(6, 7, 'Ei laskennalliset / Todistuksessa viitattu liitteisiin', 'Ei laskennalliset / Todistuksessa viitattu liitteisiin SV', 'Energiatodistusasetuksen mukaan liitteet eivät ole osa energiatodistusta, eikä todistuksessa tule viitata liitteisiin. Energiatodistus laaditaan asetuksen liitteen 3 mukaiselle lomakkeelle.  Toimenpide-ehdotukset merkitään energiatodistukseen seuraavasti: 
* Toimenpide-ehdotuksina esitetään lyhyesti energiansäästötoimenpiteet kullekin lomakkeessa yksilöidylle järjestelmälle erikseen.
* Toimenpiteelle arvioidaan sen aikaansaama muutos rakennuksen lämmitysenergian, sähköenergian ja tilojen jäähdytysenergian laskennallisessa ostoenergiankulutuksessa. Pienentynyt ostoenergian määrä esitetään negatiivisena lukuna, lisääntynyt ostoenergian määrä positiivisena lukuna.
* Toimenpiteille esitetään myös toimenpiteellä aikaansaatava muutos E-luvussa. E-luvun muutos pienemmäksi ilmoitetaan samaten negatiivisena lukuna.
(Ympäristöminiteriön asetus rakennuksen energiatodistuksesta 1048/2017, 6§)', 'TODO SV'),
(7, 8, 'Ei laskennalliset / Rakennuksen valmistumisvuosi väärin', 'Ei laskennalliset / Rakennuksen valmistumisvuosi väärin SV', 'Rakennuksen valmistumisvuosi on ilmoitettu virheellisesti. Rakennuksen valmistumisvuotena käytetään vuotta, jolloin rakennus on ensimmäistä kertaa loppukatselmuksessa hyväksytty käyttöön. Kun energiatodistus laaditaan uudelle rakennukselle rakennusluvan hakemisen yhteydessä, valmistumisvuotena käytetään todistuksessa luvan hakemisvuotta. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, liite 4)', 'TODO SV'),
(8, 9, 'Ei laskennalliset / Ylilämpenemislaskelma puuttuu (uusi rakennus)', 'Ei laskennalliset / Ylilämpenemislaskelma puuttuu (uusi rakennus) SV', 'Käytetyn laskentaohjelman oikeellisuutta ei voitu tarkastaa, sillä lähetetystä materiaalista puuttui laskennallinen kesäajan huonelämpötilatarkastelu.  Uutta rakennusta suunniteltaessa tulee tehdä energiaselvitys, joka sisältää laskennallisen kesäaikaisen huonelämpötilatarkastelun. (Ympäristöministeriön asetus uuden rakennuksen energiatehokkuudesta 1010/2017, 34 §, 29 §)', 'TODO SV'),
(9, 10, 'Ei laskennalliset / Laatimisvaihe väärin', 'Ei laskennalliset / Laatimisvaihe väärin SV', 'Energiatodistuksessa ilmoitetaan, onko energiatodistus laadittu uudelle rakennukselle rakennuslupaa haettaessa, uudelle rakennukselle käyttöönottovaiheessa  vai  olemassa  olevalle  rakennukselle.  Rakennus katsotaan otetuksi käyttöön, kun se on maankäyttö- ja rakennuslain 153 §:n 1 momentin mukaisessa loppukatselmuksessa hyväksytty käyttöönotettavaksi. Käyttöönoton jälkeen todistus on tehtävä olemassa olevan rakennuksen lainsäädännön mukaisesti. (Energitodistuslaki 50/2013, 5 § ja Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 4)', 'TODO SV'),
(10, 11, 'Rakennusvaippa / q50-luku väärin (uudet rakennukset)', 'Rakennusvaippa / q50-luku väärin (uudet rakennukset) SV', 'Rakennusvaipan ilmanvutoluku q50 on alhainen. Uusien rakennuksien ilmanpitävyyden lähtötietona käytetään rakennusvaipan ilmanvuotoluvun suunnitteluarvoa, jos ilmanpitävyys osoitetaan teollisen talonrakennuksen laadunvarmistusmenettelyllä tai tullaan osoittamaan mittaamalla. Mikäli rakennusvaipan ilmanvuotolukua ei voida edellä mainituilla tavoilla selvittää, se määritetään taulukon 4 arvojen perusteella. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, liite1)', 'TODO SV'),
(11, 12, 'Rakennusvaippa / q50-luku väärin (olemassa olevat)', 'Rakennusvaippa / q50-luku väärin (olemassa olevat) SV', 'Rakennusvaipan ilmanvuotoluku q50 on alhainen. Olemassa olevan rakennuksen rakennusvaipan ilmanvuotoluku selvitetään mittaamalla, suunnitelmista tai ajantasaisista rakennuksen asiakirjoista. Mikäli rakennusvaipan ilmanvuotolukua ei voida edellä mainituilla tavoilla selvittää, se määritetään asetuksen taulukon 4 arvojen perusteella. On hyvä muistaa, että n50 ja q50 ovat kaksi eri arvoa. (Ympäristöministeriön asetuksen rakennuksen energiatodistuksesta 1048/2017, liite1)', 'TODO SV'),
(12, 13, 'Rakennusvaippa / Nettoala laskettu virheellisesti', 'Rakennusvaippa / Nettoala laskettu virheellisesti SV', 'Rakennuksen lämmitetty nettoala poikkeaa piirustusten mukaisesta ja sen osalta laskenta tulee tarkistaa. Lämmitetty nettoala (Anetto) on lämmitettyjen kerrostasoalojen summa kerrostasoja ympäröivien ulkoseinien sisäpintojen mukaan laskettuna. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(13, 14, 'Rakennusvaippa / Alapohjan pinta-ala laskettu virheellisesti', 'Rakennusvaippa / Alapohjan pinta-ala laskettu virheellisesti SV', 'Alapohjan pinta-ala poikkeaa piirustusten mukaisesta ja sen osalta laskenta tulee tarkistaa.  Alapohjan pinta-ala lasketaan sisämittojen mukaan aukkojen ja rakenteiden aloja vähentämättä. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(14, 15, 'Rakennusvaippa / Yläpohjan pinta-ala laskettu virheellisesti', 'Rakennusvaippa / Yläpohjan pinta-ala laskettu virheellisesti SV', 'Yläpohjan pinta-ala poikkeaa piirustusten mukaisesta ja sen osalta laskenta tulee tarkistaa. Yläpohjan pinta-ala lasketaan ulkoseinien sisämittojen mukaisesti kattoikkunoiden aukkojen pinta-alat vähentäen.  (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(15, 16, 'Rakennusvaippa / Ikkunoiden pinta-ala laskettu virheellisesti', 'Rakennusvaippa / Ikkunoiden pinta-ala laskettu virheellisesti SV', 'Ikkunoiden pinta-alat poikkeavat piirustusten mukaisesta ja niiden osalta laskenta tulee tarkistaa. Ympäristöministeriön asetuksen rakennuksen energiatodistuksesta (1048/2017, Liite 1) mukaan ikkunoiden ja ovien pinta-alat lasketaan kehän eli karmirakenteen ulkomittojen mukaan.', 'TODO SV'),
(16, 17, 'Rakennusvaippa / Ulkoseinien pinta-ala laskettu virheellisesti', 'Rakennusvaippa / Ulkoseinien pinta-ala laskettu virheellisesti SV', 'Ulkoseinien pinta-alat poikkeavat piirustusten mukaisesta ja niiden osalta laskenta tulee tarkistaa. Ulkoseinien pinta-ala lasketaan sisämittojen mukaisesti alapohjan lattiapinnasta yläpohjan alapintaan ikkunoiden ja ovien aukkojen pinta-alat vähentäen. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(17, 18, 'Rakennusvaippa / U-arvoissa virhe', 'Rakennusvaippa / U-arvoissa virhe SV', 'Laskennassa käytetyt rakenteiden lämmönläpäisykertoimet (U-arvot) eivät vastaa [suunnitelmien mukaisia/ rakentamisajankohdan määräysten mukaisia] arvoja.  (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, liite 1)', 'TODO SV'),
(18, 19, 'Rakennusvaippa / Ikkunoiden gkohtisuora I', 'Rakennusvaippa / Ikkunoiden gkohtisuora I SV', 'Energiatodistuksen mukaan rakennuksen Ikkunoiden gkohtisuora on 0,85. Suomen rakentamismääräyskokoelman energiatehokkuusopas rakennuksen energiakulutuksen ja lämmitystehontarpeen laskentaan taulukon 5.1 mukaan gkohtisuora=0,85 on tyypillinen arvo yksinkertaiselle lasitukselle. Pyydämme tarkistamaan ikkunoiden gkohtisuora -arvot.', 'TODO SV'),
(19, 20, 'Rakennusvaippa / Ikkunoiden gkohtisuora II', 'Rakennusvaippa / Ikkunoiden gkohtisuora II SV', 'Ikkunoiden gkohtisuora arvo on korkea. Uusien ikkunoiden gkohtisuora arvot ovat yleensä alhaisempia. Mikäli ikkunoiden tuotetiedoissa määritettyjä arvoja ei ole käytettävissä, käytetään g-kohtisuora arvoa 0,6. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta (1048/2017, Liite 1)', 'TODO SV'),
(20, 21, 'Rakennusvaippa / Kylmäsillat virheelliset (uudet rakennukset)', 'Rakennusvaippa / Kylmäsillat virheelliset (uudet rakennukset) SV', 'Rakenteiden välisten liitosten kylmäsiltojen lämpöhäviö on laskettava. Rakenteiden välisten liitosten kylmäsiltojen ominaislämpöhäviöt ja pituudet määritetään rakennuksen asiakirjoista. Ellei tarkempaa tietoa ole käytettävissä, kylmäsiltojen laskenta voidaan tehdä energiatehokkuuden laskentaohjeen luvussa 3 esitetyn mukaisesti. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1; Energiatehokkuusopas rakennuksen energiakulutuksen ja lämmitystehontarpeen laskentaan, luku 3)', 'TODO SV'),
(21, 22, 'Rakennusvaippa / Kylmäsillat virheelliset  (olemassa olevat)', 'Rakennusvaippa / Kylmäsillat virheelliset  (olemassa olevat) SV', 'Kylmäsiltojen lämpöhäviö puuttuu. Rakenteiden välisten liitosten kylmäsiltojen lämpöhäviö on laskettava. Olemassa oleville rakennuksille kylmäsiltojen vaikutus voidaan arvioida yksinkertaistetusti lisäämällä 10 % ulkovaipan johtumislämpöhäviöön. (Ympäristöministeriön asetuksen rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(22, 23, 'Ilmanvaihto / Ilmavirrat virheelliset', 'Ilmanvaihto / Ilmavirrat virheelliset SV', 'Ilmanvaihdon energiankulutuksen laskennassa tulee käyttää vakioidun käytön mukaisia ilmamääriä sekä käyttöaikoja. (Ympäristöministeriön asetus rakennuksen energiatehokkuudesta 1010/2017, 10 § ja 11 §)', 'TODO SV'),
(23, 24, 'Ilmanvaihto / SFP-luku virheellinen', 'Ilmanvaihto / SFP-luku virheellinen SV', 'Ilmanvaihtojärjestelmän ominaissähkötehosta (SFP-luku) [ei ole tehty selvitystä/on virheellisesti laskettu]. SFP-luku voidaan laskea laitetietoja apuna käyttäen. Uusissa rakennuksissa käytetään suunnitelmien arvoja. Mikäli ilmanvaihtojärjestelmän ominaissähkötehoa ei voida selvittää, käytetään asetuksen liitteen taulukon 3 arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, liite 1)', 'TODO SV'),
(24, 25, 'Ilmanvaihto / SFP-luku yli vaatimusten (uudet rak.)', 'Ilmanvaihto / SFP-luku yli vaatimusten (uudet rak.) SV', 'Ilmanvaihdon ominaissähköteho (SFP-luku) ei vastaa uudisrakentamiselle asetettuja vaatimuksia. (Ympäristöministeriön asetus rakennuksen energiatehokkuudesta 1010/2017, 30 §)', 'TODO SV'),
(25, 26, 'Ilmanvaihto / Lämmöntalteenoton vuosihyötysuhde virheellinen', 'Ilmanvaihto / Lämmöntalteenoton vuosihyötysuhde virheellinen SV', 'Ilmanvaihtojärjestelmän lämmöntalteenoton vuosihyötysuhde on korkea ja [siitä ei ole tehty selvitystä/se on virheellisesti laskettu]. Ilmanvaihdon LTO:n vuosihyötysuhde voidaan laskea laitetietojen avulla Ympäristöministeriön exceliä apuna käyttäen. Uusissa rakennuksissa käytetään suunnitelmien arvoja. Mikäli ilmanvaihtojärjestelmän LTO:n vuosihyötysuhdetta ei voida selvittää, käytetään asetuksen liitteen taulukon 2 arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, liite 1)', 'TODO SV'),
(26, 27, 'Ilmanvaihto / SFP-luku ja LTO vuosihyötysuhde VTT-arvot', 'Ilmanvaihto / SFP-luku ja LTO vuosihyötysuhde VTT-arvot SV', 'Ilmanvaihtokoneen kokonaisvuosihyötysuhteesta eikä SFP-luvusta ole esitetty laskelmaa. Kokonaisvuosihyötysuhde ja SFP-luku ovat rakennuskohtaisia, joten ne eivät voi olla VTT:n antamia arvoja.', 'TODO SV'),
(27, 28, 'Ilmanvaihto / Ilmanvaihdon lämmitysenergiankulutus virheellinen', 'Ilmanvaihto / Ilmanvaihdon lämmitysenergiankulutus virheellinen SV', 'Ilmanvaihdon lämmitysenergiankulutus on [alhainen/korkea]. Ilmanvaihdon energiankulutuksen laskennassa tulee käyttää vakioidun käytön mukaisia ilmamääriä sekä käyttöaikoja ja ilmoitettua vuosihyötysuhdetta. (Ympäristöministeriön asetus rakennuksen energiatehokkuudesta 1010/2017, 10 § ja 11 §)', 'TODO SV'),
(28, 29, 'Ilmanvaihto / Ilmavaihdon sähkönkulutus virheellinen', 'Ilmanvaihto / Ilmavaihdon sähkönkulutus virheellinen SV', 'Ilmanvaihdon sähköenergiankulutus on [alhainen/korkea]. Ilmanvaihdon energiankulutuksen laskennassa tulee käyttää vakioidun käytön mukaisia ilmamääriä sekä käyttöaikoja ja ilmoitettua ilmanvaihtojärjestelmän SFP-lukua. (Ympäristöministeriön asetus rakennuksen energiatehokkuudesta 1010/2017, 10 § ja 11 §)', 'TODO SV'),
(29, 30, 'Ilmanvaihto / Painovoimainen iv huomioimatta', 'Ilmanvaihto / Painovoimainen iv huomioimatta SV', 'Ilmanvaihdon lämmitysenergiankulutusta ei ole huomioitu laskennassa. Ilmanvaihdon energiankulutuksen laskennassa käytetään ilmamäärinä vakioidun käytön mukaisia arvoja. Painovoimaisen ilmavaihdon tapauksessa tuloilma on ulkoa otettavaa korvausilmaa. Tiloissa tapahtuva tuloilman ja korvausilman lämpeneminen on osa tilojen lämmitysenergiantarvetta ja huomioidaan laskennassa siinä yhteydessä. (Ympäristöminiteriön asetus rakennuksen energiatehokkuudesta 1010/2017, 10 §)', 'TODO SV'),
(30, 31, 'Ilmanvaihto / Ilmvirrat, pientalot', 'Ilmanvaihto / Ilmvirrat, pientalot SV', 'Ympäristöministeriön asetus uuden rakennuksen energiatehokkuudesta (1010/2017) 10 § mukaan E-luku on laskettava käyttäen seuraavia käyttöajan ulkoilmavirtoja, Käyttötarkoitusluokka 1) 0,4 m³/(s m²). (huom! kohta ilmavirta tulo/poisto)', 'TODO SV'),
(31, 32, 'Ilmanvaihto / Sisäänpuhalluslämpötila "virheellinen"', 'Ilmanvaihto / Sisäänpuhalluslämpötila "virheellinen" SV', 'Annetun vuosihyötysuhteen ja vakioidun käytön mukaan laskettu ilmanvaihdon lämmitysenergiantarve on alhainen. Energiatehokkuus-laskentaoppaan mukaan tuloilman sisäänpuhalluslämpötilana tilaan voidaan käyttää 18 °C, ellei tarkempaa tietoa ole saatavilla. Jos halutaan käyttää tätä alhaisempia sisäänpuhalluslämpötiloja lämmityskaudella, tulee siitä tehdä selvitys, mm. miten tilakohtaisten lämmityslaitteiden mitoituksessa on huomioitu tuloilman lämmitystarve.  (Energiatehokkuusopas rakennuksen energiakulutuksen ja lämmitystehontarpeen laskentaan, luku 3)', 'TODO SV'),
(32, 33, 'Ilmanvaihto / Lämpötilahyötysuhde = vuosihyötysuhde', 'Ilmanvaihto / Lämpötilahyötysuhde = vuosihyötysuhde SV', 'Tarkasta ilmanvaihtokoneen LTO:n lämpötilahyötysuhteen arvo se ei voi olla [sama/pienempi] mitä LTO:n vuosihyötysuhde.', 'TODO SV'),
(33, 34, 'Ilmanvaihto / LTO:n vuosihyötysuhteen laskennassa virheitä', 'Ilmanvaihto / LTO:n vuosihyötysuhteen laskennassa virheitä SV', 'Lämmöntalteenoton vuosihyötysuhde on yläkanttiin laskettu. Lähetetyn materiaalin mukaisessa ilmanvaihtokoneessa on vastavirtasiirrin, jonka jäätymisen esto on lähempänä +0 °C, kuin -10 °C. Jäätymisen eston ulospuhallusilman minimilämpötilana voidaan käyttää Energiatehokkuus-oppaan mukaisia ohjearvoja, jos laitteesta ei ole olemassa varmennettuja suoritusarvoja. LTO-vuosihyötysuhteen laskennassa on oletettu myös, että kaikki ilmanvaihto on käytön ulkopuolella kiinni, tämä on vastoin Ympäristöministeriön asetusta uuden rakennuksen sisäilmastosta ja ilmanvaihdosta (1009/2017).', 'TODO SV'),
(34, 35, 'Käyttövesi / LKV:n jaon ja luovutuksen hyötysuhde', 'Käyttövesi / LKV:n jaon ja luovutuksen hyötysuhde SV', 'Lämpimän käyttöveden jakelun hyötysuhde poikkeaa taulukkoarvosta ja siitä ei ole tehty selvitystä. LKV:n jakelun hyötysuhde voidaan selvittää erillisselvityksellä, jolloin arvoa käytetään laskennassa. Jos jakelun hyötysuhdetta ei ole selvitetty, käytetään hyötysuhteena asetuksen liitteen taulukon 5 arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(35, 36, 'Käyttövesi / Lämmityksen tuoton hyötysuhde, käyttövesi', 'Käyttövesi / Lämmityksen tuoton hyötysuhde, käyttövesi SV', 'Lämmöntuottojärjestelmän hyötysuhde poikkeaa taulukkoarvosta käyttöveden lämmityksen osalta ja siitä ei ole tehty selvitystä. Tuoton hyötysuhde voidaan selvittää rakennuksen tarkastuksen yhteydessä tai tuotetiedoista, jolloin arvoa käytetään laskennassa. Jos hyötysuhdetta ei ole selvitetty, käytetään lämmöntuottojärjestelmän hyötysuhteina taulukoissa 10 ja 11 esitettyjä arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2018, Liite 1)', 'TODO SV'),
(36, 37, 'Käyttövesi / Kiertovesipumpun sähkönkulutus puuttuu', 'Käyttövesi / Kiertovesipumpun sähkönkulutus puuttuu SV', 'Lämpimän käyttöveden kiertovesipumpun sähkönkulutus puuttuu. Lämpimän käyttöveden kiertovesipumpun sähköenergian kulutus lasketaan energiatehokkuuden laskentaohjeen kohdan 6.3.4 mukaan tai muulla vastaavalla tavalla.  (Energiatehokkuusopas rakennuksen energiakulutuksen ja lämmitystehontarpeen laskentaan, luku 6)', 'TODO SV'),
(37, 38, 'Käyttövesi / LKV:n kiertojohto laskettu virheellisesti', 'Käyttövesi / LKV:n kiertojohto laskettu virheellisesti SV', 'Lämpimän käyttöveden kiertojohdon lämpöhäviöt [ovat alhaiset/puuttuvat]. LKV:n kiertojohdon häviöt voidaan selvittää erillisselvityksellä, jolloin saatua arvoa tulee käyttää laskennassa. Muussa tapauksessa se määritetään asetuksen liitteen taulukoiden 6 ja 7 avulla. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(38, 39, 'Käyttövesi / LKV:n kiertojohto II asuinkerrostalot', 'Käyttövesi / LKV:n kiertojohto II asuinkerrostalot SV', 'Lämpimän käyttöveden kiertojohdon lämpöhäviöt [ovat alhaiset/puuttuvat]. Jos häviöt laskee putkipituuden mukaan, tulee putkipituuteen huomioida kiertojohdon lisäksi myös lämpimän käyttöden jakojohto, siltä osin, kun siinä on kiertoa (asuntojen vesimittareille asti). Laskenta tulee tarkistaa tältä osin.', 'TODO SV'),
(39, 40, 'Käyttövesi / Käyttöveden energiankulutus virheellinen I', 'Käyttövesi / Käyttöveden energiankulutus virheellinen I SV', 'Lämpimän käyttöveden energiankulutus ei vastaa vakioidun käytön mukaan laskettua. Lämpimän käyttöveden nettoenergiantarpeena käytetään asetuksen mukaisia vakioidun käytön arvoja. (Ympäristöminiteriön asetus uuden rakennuksen energiatehokkuudesta 1010/2017, 12 §)', 'TODO SV'),
(40, 41, 'Käyttövesi / Käyttöveden energiankulutus virheellinen II', 'Käyttövesi / Käyttöveden energiankulutus virheellinen II SV', 'Käyttöveden lämmitysenergian nettotarve on korkea. Käyttötarkoitusluokassa 1 on lämpimän käyttöveden lämmitysenergian nettotarve enintään 4 200 kWh vuodessa asuntoa kohden. (Ympäristöministeriön asetus uuden rakennuksen energiatehokkuudesta 1010/2017, 12 §)', 'TODO SV'),
(41, 42, 'Käyttövesi / Lämpöpumpun SPF-luku käyttövesi', 'Käyttövesi / Lämpöpumpun SPF-luku käyttövesi SV', 'Lämpöpumpun lämpökerroin on käyttöveden osalta korkea, eikä siitä ole annettu lisäselvitystä. Lämpöpumpun kausisuorituskykykertoimet (SPF-luvut) voidaan selvittää  suunnitelmista tai tuotetiedoista. Ellei tämä ole mahdollista, käytetään asetuksen liitteen 1 taulukoiden 12-14 arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(42, 43, 'Tilojen lämmitys / Tilojen lämmityksen jaon ja luovutuksen hyötysuhde', 'Tilojen lämmitys / Tilojen lämmityksen jaon ja luovutuksen hyötysuhde SV', 'Lämmitysjärjestelmien lämmönjaon ja -luovutuksen vuosihyötysuhde poikkeaa taulukkoarvosta ja siitä ei ole tehty selvitystä. Vuosihyötysuhde voidaan selvittää rakennuksen tarkastuksen yhteydessä, jolloin arvoa käytetään laskennassa. Muussa tapauksessa laskennassa käytetään liitteen 1 taulukossa 9 esitettyjä  lämmönjaon ja -luovutuksen vuosihyötysuhteiden arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, liite1)', 'TODO SV'),
(43, 44, 'Tilojen lämmitys / Lämmityksen tuoton hyötysuhde, tilojen lämmitys', 'Tilojen lämmitys / Lämmityksen tuoton hyötysuhde, tilojen lämmitys SV', 'Lämmöntuottojärjestelmän hyötysuhde poikkeaa taulukkoarvosta tilojen lämmityksen osalta ja siitä ei ole tehty selvitystä. Tuoton hyötysuhde voidaan selvittää rakennuksen tarkastuksen yhteydessä tai tuotetiedoista, jolloin arvoa käytetään laskennassa. Jos hyötysuhdetta ei ole selvitetty, käytetään lämmöntuottojärjestelmän hyötysuhteina taulukoissa 10 ja 11 esitettyjä arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2018, Liite 1)', 'TODO SV'),
(44, 45, 'Tilojen lämmitys / Tilojen lämmityksen apulaitteet', 'Tilojen lämmitys / Tilojen lämmityksen apulaitteet SV', 'Lämmitysjärjestelmän apulaitteiden sähkökulutus [puuttuu/on alhainen]. Lämmitysjärjestelmän apulaitteiden sähkönkulutus muodostuu lämmönjaon ja -luovutuksen- sekä tuoton apulaitteiden sähkönkulutuksesta. Apulaitteiden sähkönkäyttö voidaan selvittää rakennuksen tarkastuksen yhteydessä, jolloin arvoa käytetään laskennassa. Muuten apulaitteiden sähkönkulutus lasketaan taulukoissa 9, 10 ja 11 esitettyjen lukujen avulla. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(45, 46, 'Tilojen lämmitys / Lämpöpumpun SPF-luku tilalämmitys', 'Tilojen lämmitys / Lämpöpumpun SPF-luku tilalämmitys SV', 'Lämpöpumpun lämpökerroin on tilojen lämmityksen osalta korkea, eikä siitä ole annettu lisäselvitystä. Lämpöpumpun kausisuorituskykykertoimet (SPF-luvut) voidaan selvittää  suunnitelmista tai tuotetiedoista. Ellei tämä ole mahdollista, käytetään asetuksen liitteen 1 taulukoiden 12-14 arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(46, 47, 'Tilojen lämmitys / Rakennuksen ulkopuolisten lämpökanaalien häviöt', 'Tilojen lämmitys / Rakennuksen ulkopuolisten lämpökanaalien häviöt SV', 'Rakennuksen ulkopuolisia lämpökanaaleja ei ole huomioitu laskennassa. Jos rakennus on kytketty lämmitysjärjestelmään (tilojen, ilmanvaihdon ja lämpimän käyttöveden lämmitys), jossa lämpö johdetaan rakennuksen
ulkopuolisilla lämpöputkilla yhteisestä lämmönsiirtimestä tai lämmöntuottolaitteesta useampaan rakennukseen, on kyseisten lämpöputkien lämpöhäviö jaettava rakennusten kesken
pinta-alojen suhteessa. (Ympäristöministeriön asetus rakennuksen energiatehokkuusasetus 1010/2017, 18 §)', 'TODO SV'),
(47, 48, 'Tilojen lämmitys / Maatilan omaan käyttöön tuottama aluelämpö ei ole kaukolämpöä', 'Tilojen lämmitys / Maatilan omaan käyttöön tuottama aluelämpö ei ole kaukolämpöä SV', 'Kaukolämpö on jakeluverkon kautta asiakkaan kiinteistölle toimitettua lämpöä. Maatilalla tuotettua aluelämpöä ei voida rinnastaa energiatodistuslaskennassa kaukolämpöön. (Ympäristöminiteriön asetus uuden rakennuksen energiatehokkuudesta 1010/2017, 2 §)', 'TODO SV'),
(48, 49, 'Tilojen lämmitys / Ilmalämpöpumppu', 'Tilojen lämmitys / Ilmalämpöpumppu SV', 'Ilmalämpöpumpulla tuotettu energiamäärä on liian suuri. Pienille asuinrakennuksille (käyttötarkoitusluokka 1) käytetään ilma-ilmalämpöpumpun tuottamalle energialle enintään asetuksen liitteen 1 taulukossa 15 esitettyjä arvoja.  (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 1)', 'TODO SV'),
(49, 50, 'Tilojen lämmitys / COP vs. SPF', 'Tilojen lämmitys / COP vs. SPF SV', 'Lämpöpumpun erillisselvityksessä on ilmoitettu lämpökertoimet (COP) lämmitykselle ja lämpimän käyttöveden valmistukselle. Lämpökerroin ei ole sama asia, mitä lämpöpumpun kausisuorituskykykerroin (SPF-luku). COP kertoo hetkellisen hyötysuhteen tietyssä lämpötilassa, kun taas SPF-luku kertoo keskimääräisen hyötysuhteen vuoden aikana huomioiden sääolosuhteet ja lämmitysverkoston lämpötilan. Vuosihyötysuhteen laskennasta on kerrottu enemmän Ympäristöministeriön sivulta löytyvästä Lämpöpumppujen energialaskentaoppaassa. Lämpöpumppujen SPF-luvut voidaan selvittää  suunnitelmista tai tuotetiedoista, ellei tämä ole mahdollista, käytetään asetuksen liitteen 1 taulukoiden 12-14 arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta (1048/2017, liite 1)', 'TODO SV'),
(50, 51, 'Vakioitu käyttö ja lämpökuormat / Valaistus I', 'Vakioitu käyttö ja lämpökuormat / Valaistus I SV', 'Valaistuksen lämpökuorma poikkeaa asetuksen taulukkoarvosta ja siitä ei ole tehty selvitystä. Valaistuksen lämpökuorman arvona voidaan käyttää valaistussuunnitelman mukaista arvoa, jos valaistussuunnitelmasta voidaan lämpökuorma määrittää tilatyyppikohtaisesti valaistuksen tehotiheyden ja valaistuksen ohjauksen perusteella. (Ympäristöministeriön asetus rakennuksen energiatehokkuudesta 1010/2017, 11 §)', 'TODO SV'),
(51, 52, 'Vakioitu käyttö ja lämpökuormat / Valaistus II, asuinkerrostalot', 'Vakioitu käyttö ja lämpökuormat / Valaistus II, asuinkerrostalot SV', 'Laskennassa käytetty valaistuksen tehotaso poikkeaa asetuksen taulukkoarvosta. Asuinkerrostalossa, jossa asukkaalla on mahdollista tuoda omat valaisimet, on asuinhuoneissa käytettävä vakioidun käytön mukaisia arvoja. Yleisissä tiloissa sekä asuntojen pesuhuoneissa voidaan käyttää suunniteltuja valaistustasoja. (Ympäristöministeriön asetus rakennuksen energiatehokkuudesta 1010/2017, 11 §)', 'TODO SV'),
(52, 53, 'Vakioitu käyttö ja lämpökuormat / Lämpökuormat laskettu väärin', 'Vakioitu käyttö ja lämpökuormat / Lämpökuormat laskettu väärin SV', 'Laskennassa tulee käyttää Ympäristöministeriön asetus rakennuksen energiatehokkuudesta (1010/2017) 11 § mukaisia valaistuksen, kuluttajalaitteiden ja ihmisten aiheuttamia  lämpökuormia.', 'TODO SV'),
(53, 54, 'Vakioitu käyttö ja lämpökuormat / Poikkeavat lähtötiedot', 'Vakioitu käyttö ja lämpökuormat / Poikkeavat lähtötiedot SV', 'Mikäli energiatodistuksen laadinnassa on käytetty laitteiden tuotetietoihin pohjautuvia laskennan lähtöarvoja, jotka poikkeavat energiatodistusasetuksen laskentaliitteen tai energiatehokkuuden laskentaohjeen arvoista, lisämerkinnöissä on esitettävä laitteiden valmistajat ja muut laitetiedot sekä käytetyt laskennan lähtöarvot. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, Liite 4)', 'TODO SV'),
(54, 55, 'Vakioitu käyttö ja lämpökuormat / Varastorakennuksen kuormat', 'Vakioitu käyttö ja lämpökuormat / Varastorakennuksen kuormat SV', 'Varastorakennusten E-luku lasketaan uusille rakennuksille suunnitteluarvoilla. Kuormien käyttöasteet ja aikataulut tulee tarkastaa, nyt laskennassa on käytetty omakotitalolle tyypillisiä vakioidun käytön arvoja. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta (1048/2017)', 'TODO SV'),
(55, 56, 'Jäähdytys / Väärä laskentamenetelmä, jäähdytys', 'Jäähdytys / Väärä laskentamenetelmä, jäähdytys SV', 'Todistus on tehty virheellisesti uudesta jäähdytetystä rakennuksesta kuukausitason laskenta-menetelmällä.  Kuukausitason laskentamenetelmää voidaan käyttää rakennukselle, jonka sisäilman lämpötilan hallinta ei edellytä jäähdytystä tai jäähdytystä edellytetään vain tiloissa, joiden lämmitetty nettoala on alle 10 prosenttia rakennuksen lämmitetystä nettoalasta tai joiden lämmitetty nettoala on alle 50 neliömetriä. Huonelämpötilatarkastelun puuttuessa, ei voida todeta etteikö rakennuksen sisäilman lämpötilan hallinta edellyttäisi jäähdytystä. Jäähdytys tulee huomioida ja laskennassa on käytettävä dynaamista laskentamenetelmää. (Ympäristöministeriön asetus energiatehokkuudesta 1010/2017, 8 §).', 'TODO SV'),
(56, 57, 'Jäähdytys / Jäähdytysjärjestelmän kylmäkerroin', 'Jäähdytys / Jäähdytysjärjestelmän kylmäkerroin SV', 'Jäähdytysjärjestelmän painotettu kylmäkerroin ei ole sama asia mitä jäähdytyskoneen COP. COP kertoo hetkellisen hyötysuhteen tietyssä lämpötilassa, kun taas painotettu kylmäkerroin kertoo jäähdytyksen keskimääräisen hyötysuhteen jäähdytyskauden aikana. Jos laitevalmistajan varmennettuja suoritusarvoja ei ole käytettävissä, voi laskennassa käyttää energiatehokkuus-laskentaoppaan taulukossa 9.2 annettuja arvoja. (Energiatehokkuusopas rakennuksen energiakulutuksen ja lämmitystehontarpeen laskentaan, luku 9)', 'TODO SV'),
(57, 58, 'Toimenpide-ehdotukset / Paikan päällä ei ole käyty', 'Toimenpide-ehdotukset / Paikan päällä ei ole käyty SV', 'ARAn näkemyksen mukaan laatija ei ole itse havainnoinut paikan päällä. Rakennuksen tyypilliseen käyttöön tarvittavan energiamäärän selvittämiseksi sekä suositusten laatimiseksi on energiatodistuksen laatijan selvitettävä olemassa olevan rakennuksen energiankulutukseen vaikuttavat ominaisuudet asiakirjoista, havainnoimalla paikan päällä todistuksen kohdetta sekä tarvittaessa haastattelemalla rakennuksen käyttäjiä tai ylläpitohenkilökuntaa.  (Laki rakennuksen energiatodistuksesta 50/2013, 11§)', 'TODO SV'),
(58, 59, 'Toimenpide-ehdotukset / Toimenpide-ehdotukset puuttuu', 'Toimenpide-ehdotukset / Toimenpide-ehdotukset puuttuu SV', 'Energiatodistuksessa ei ole annettu toimenpide-ehdotuksia. Todistuksessa annetaan suosituksia toimista, joilla voidaan parantaa kustannustehokkaasti rakennuksen energiatehokkuutta, ellei kyseessä ole uudisrakennus tai rakennus, jolle ei tällaisia toimia voida osoittaa. (Laki rakennuksen energiatodistuksesta 50/2013, 9 §)', 'TODO SV'),
(59, 60, 'Muut / Aurinkopaneelit ja -keräimet', 'Muut / Aurinkopaneelit ja -keräimet SV', 'Aurinkopaneeleista ja -keräimistä otetaan laskennassa huomioon vain se osa, joka voidaan rakennuksessa käyttää hyödyksi eli se osuus, joka pienentää ostoenergiantarvetta. Ulkopuolisiin energiaverkkoihin syötettyä energiaa ei oteta laskennassa huomioon, joten se ei vaikuta E-luvun arvoon. Hyödynnettävästä energiasta pitää esittää erillinen laskelma. (Ympäristöministeriön asetus rakennuksen energiatodistuksesta 1048/2017, liite 1)', 'TODO SV'),
(60, 61, 'Muut / E-lukulaskenta tehty virheellisesti', 'Muut / E-lukulaskenta tehty virheellisesti SV', 'E-luvun laskenta on suurilta osin virheellinen, joten laskennan virheitä ei lähdetä tässä tarkastusmuistiossa erittelemään. Ympäristöministeriö on julkaissut uudet energiatodistusten laskentaesimerkit Motivan sivuilla. Teidän tulee tarkastaa, että käyttämänne laskentaohjelma antaa esimerkkejä vastaavat tulokset laskennassa käytetyillä lähtöarvoilla.', 'TODO SV'),
(61, 62, 'Muut / Lähtötiedot ovat virheelliset', 'Muut / Lähtötiedot ovat virheelliset SV', 'E-luvun laskennan lähtötiedot ovat suurilta osin virheellisiä, joten lähtötietojen virheitä ei lähdetä tässä tarkastusmuistiossa erittelemään.', 'TODO SV'),
(62, 63, 'Muut / Lähtötietoja ei ole toimitettu', 'Muut / Lähtötietoja ei ole toimitettu SV', 'Laatija ei ole toimittanut pyynnöstä huolimatta taustamateriaa. Valvontamuistio on tehty pelkän energiatodistuksen pohjalta', 'TODO SV'),
(63, 64, 'Muut / Jokeri (luokittelematon virhe)', 'Muut / Jokeri (luokittelematon virhe) SV', '', '')
